(function(){tinymce.PluginManager.requireLangPack('subordinate');tinymce.create('tinymce.plugins.SubordinatePlugin',{init:function(ed,url){ed.addCommand('mceSubordinate',function(){$('#msm_subordinate').dialog({open:function(event,ui){$(".ui-dialog-titlebar-close").hide();$("#msm_subordinate_highlighted").val(ed.selection.getContent({format:'text'}))},modal:true,autoOpen:false,height:500,width:750,closeOnEscape:false});$('#msm_subordinate').dialog('open').css('display','block')});ed.onNodeChange.add(function(ed,cm,n){if(ed.selection.getContent()){cm.setDisabled('subordinate',false)}else{cm.setActive('subordinate',false);cm.setDisabled('subordinate',true)}});ed.addButton('subordinate',{title:'subordinate.desc',cmd:'mceSubordinate',image:url+'/img/subordinate.png'})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Subordinate plugin',author:'Ga Young Kim',authorurl:'http://ualberta.ca ',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/subordinate',version:"1.0"}}});tinymce.PluginManager.add('subordinate',tinymce.plugins.SubordinatePlugin)})();